$db->hide_errors();
require_once(DIR . '/includes/class_dbalter.php');
$db_alter =& new vB_Database_Alter_MySQL($db);

$db->query_write("DROP TABLE `" . TABLE_PREFIX . "ssgti_stocktrader`");
