$db->query_write("DROP TABLE `" . TABLE_PREFIX . "ssgti_stocktrader_iislo`");
$db->query_write("DROP TABLE `" . TABLE_PREFIX . "ssgti_stocktrader_cache`");
$db->query_write("DROP TABLE `" . TABLE_PREFIX . "ssgti_stocktrader_global`");

if ($db_alter->fetch_table_info('usergroup'))
{
	$db_alter->drop_field('ssgti_stocktrader_perms');
	$db_alter->drop_field('ssgtistocktrader_exrate');
	$db_alter->drop_field('ssgtistocktrader_compp');
	$db_alter->drop_field('ssgtistocktrader_compfr');
	$db_alter->drop_field('ssgtistocktrader_comsp');
	$db_alter->drop_field('ssgtistocktrader_comsfr');
	$db_alter->drop_field('ssgtistocktrader_cgt');
	$db_alter->drop_field('ssgtistocktrader_maxstc');
	$db_alter->drop_field('ssgtistocktrader_maxshr');
	$db_alter->drop_field('ssgtistocktrader_itr');
	$db_alter->drop_field('ssgtistocktrader_stocks');
}

if ($db_alter->fetch_table_info('user'))
{
	$db_alter->drop_field('ssgti_stocktrader');
}

