			$ssgti_stocktrader_result = $vbulletin->db->query_first_slave("
				SELECT blockid, requirement
				FROM " . TABLE_PREFIX . "profileblockprivacy
				WHERE userid = " . intval($vbulletin->userinfo['userid']) . "
					AND blockid = 'ssgti_stocktrader'
			");
		}
		else
		{
			if (isset($vbulletin->GPC['blockprivacy']['ssgti_stocktrader']))
			{
				$vbulletin->db->query_write($sql = "
					REPLACE INTO " . TABLE_PREFIX . "profileblockprivacy
						(userid, blockid, requirement)
					VALUES (" . $vbulletin->userinfo['userid'] . ", 'ssgti_stocktrader', " . $vbulletin->GPC['blockprivacy']['ssgti_stocktrader'] . ")
				");
			}

			$ssgti_stocktrader_result['requirement'] = $vbulletin->GPC['blockprivacy']['ssgti_stocktrader'];
		}

		$ssgti_stocktrader_selected = array((isset($ssgti_stocktrader_result['requirement']) ? $ssgti_stocktrader_result['requirement'] : 0) => 'selected="selected"');
		eval('$profileprivacybits .= "' . fetch_template('ssgti_stocktrader_modifyprivacy_bit') . '";');
