				$checked["$key"] = 'checked="checked"';
			}
			else
			{
				$checked["$key"] = '';
			}
		}

		// draw cp nav bar
		construct_usercp_nav('ssgti_stocktrader');

		$navbits[''] = $vbphrase['ssgti_stocktrader_edit_options'];
		$templatename = 'ssgti_stocktrader_modifyoptions';
	}
	else
	{
		print_no_permission();
	}
