// ####################### start update Stock Trader options ########################
if ($_POST['do'] == 'update' . $vbulletin->options['ssgti_stocktrader_variable'])
{
	if ($vbulletin->options['ssgti_stocktrader_active'] AND !in_array($vbulletin->userinfo['userid'], explode(',', $vbulletin->options['ssgti_stocktrader_exclude_users'])) AND ($vbulletin->userinfo['permissions']['ssgti_stocktrader_perms'] & $vbulletin->bf_ugp_ssgti_stocktrader_perms['canview']))
	{
		$vbulletin->input->clean_array_gpc('p', array(
			'options'        => TYPE_ARRAY_BOOL,
			'set_options'    => TYPE_ARRAY_BOOL
		));

		$userdata =& datamanager_init('User', $vbulletin, ERRTYPE_STANDARD);
		$userdata->set_existing($vbulletin->userinfo);

		if (isset($vbulletin->GPC['options']['ssgti_stocktrader_active']) OR isset($vbulletin->GPC['set_options']['ssgti_stocktrader_active']))
		{
			$userdata->set('ssgti_stocktrader_active', $vbulletin->GPC['options']['ssgti_stocktrader_active']);
		}

		$userdata->save();

		$vbulletin->url = $vbulletin->options['ssgti_stocktrader_filename'] . '.php' . $vbulletin->session->vars['sessionurl_q'];
		eval(print_standard_redirect('redirect_updatethanks'));
	}
	else
	{
		print_no_permission();
	}
}

if ($this->registry->options['ssgti_stocktrader_active'] AND !in_array($this->registry->userinfo['userid'], explode(',', $this->registry->options['ssgti_stocktrader_exclude_users'])) AND ($this->registry->userinfo['permissions']['ssgti_stocktrader_perms'] & $this->registry->bf_ugp_ssgti_stocktrader_perms['canview']) AND $this->registry->userinfo['ssgti_stocktrader_active'])
{
	$this->validfields['ssgti_cgwm_active']  = array(TYPE_UINT, REQ_NO);
}
